import axios from "axios";
import { useState } from "react";
import { useLocation } from "react-router-dom";
import { Link } from "react-router-dom";
import { useEffect } from "react";
const Tv = () => {
    const [seasons, setSeasons] = useState([]);

    const location = useLocation();
    useEffect(() => {
        async function getData() {
            const response =
                await axios.get(`https://api.themoviedb.org/3/tv/${location.state.id}?api_key=692ad17fefe3f279a5bc0dfa87752845&language=en-US
            `);

            setSeasons(response.data);
            console.log(response);
        }
        getData();
    }, []);

    return (
        <div className="tv">
            <div className="container">
                <img
                    src={
                        "https://image.tmdb.org/t/p/w500" + seasons.poster_path
                    }
                    height="23"
                />
            </div>
            <div className="container">
                <div className="row row-cols-lg-5 row-cols-md-4 row-cols-2 ">
                    {seasons.seasons &&
                        seasons.seasons.map((season) => {
                            return (
                                <div
                                    key={season.id}
                                    className="card  mt-3"
                                    style={{ width: "18rem" }}
                                >
                                    <img
                                        className="card-img-top"
                                        src={
                                            "https://image.tmdb.org/t/p/w500" +
                                            season.poster_path
                                        }
                                        alt="Card image cap"
                                    />
                                    <div className="card-body">
                                        <h5 className="card-title text-light">
                                            {season.name}
                                        </h5>
                                        <p className="card-text text-light">
                                            {season.episode_count} Episodes
                                        </p>
                                        <Link
                                            to="/episodes"
                                            state={season.episode_count}
                                        >
                                            <a
                                                href="#"
                                                className="btn btn-primary"
                                            >
                                                Watch {season.name}
                                            </a>
                                        </Link>
                                    </div>
                                </div>
                            );
                        })}
                </div>{" "}
            </div>
        </div>
    );
};

export default Tv;
