import axios from "axios";
import { useState } from "react";
import { useLocation } from "react-router-dom";
import { Link } from "react-router-dom";
const Tv = () => {
    const [seasons, setSeasons] = useState([]);
    const location = useLocation();
    async function getData() {
        const response =
            await axios.get(`https://api.themoviedb.org/3/tv/${location.state.id}?api_key=692ad17fefe3f279a5bc0dfa87752845&language=en-US
        `);
        const getSeasons = response.data.seasons;
        // setSeasons(new Array(getSeasons));
        console.log(response);
    }
    getData();
    return (
        <div className="tv">
            <h1>tv</h1>
        </div>
    );
};

export default Tv;
