import axios from "axios";
import { useState } from "react";
import { useLocation } from "react-router-dom";
import { Link } from "react-router-dom";
import { useEffect } from "react";
const Tv = () => {
    const [seasons, setSeasons] = useState([]);
    const location = useLocation();
    useEffect(() => {
        async function getData() {
            const response =
                await axios.get(`https://api.themoviedb.org/3/tv/${location.state.id}?api_key=692ad17fefe3f279a5bc0dfa87752845&language=en-US
            `);
            const getSeasons = response.data.seasons;
            setSeasons(getSeasons);
            console.log(getSeasons);
        }
        getData();
    }, []);

    return (
        <div className="tv">
            {seasons.map((season) => {
                return (
                    <div className="card" style={{ width: "18rem" }}>
                        <img
                            className="card-img-top"
                            src={
                                "https://image.tmdb.org/t/p/w500" +
                                season.poster_path
                            }
                            alt="Card image cap"
                        />
                        <div className="card-body">
                            <h5 className="card-title">{season.name}</h5>
                            <p className="card-text">{season.episode_count}</p>
                            <a href="#" className="btn btn-primary">
                                Go somewhere
                            </a>
                        </div>
                    </div>
                );
            })}
        </div>
    );
};

export default Tv;
