import { useEffect, useState } from "react";
import Card from "./Card";
import axios from "axios";
import { Link, useLocation } from "react-router-dom";

const Home = () => {
    const [imgsArr, setImg] = useState([]);
    useEffect(() => {
        async function getData() {
            const response = await axios.get(
                "https://api.themoviedb.org/3/movie/popular/?api_key=692ad17fefe3f279a5bc0dfa87752845"
            );
            const moviesArr = response.data.results;
            setImg(moviesArr);
            console.log(imgsArr);
        }
        getData();
    }, []);

    return (
        <div className="container mt-5 ">
            <div className="row row-cols-lg-5 row-cols-md-4 row-cols-2">
                {imgsArr.map((img) => {
                    const literallyImg = img.poster_path;
                    return (
                        <Link>
                            <Card
                                image={
                                    "https://image.tmdb.org/t/p/w500" +
                                    literallyImg
                                }
                            ></Card>
                        </Link>
                    );
                })}
            </div>
        </div>
    );
};

export default Home;
