import axios from "axios";
import { useState } from "react";
import { useLocation } from "react-router-dom";
import { Link } from "react-router-dom";
import { useEffect } from "react";
const Tv = () => {
    const [seasons, setSeasons] = useState([]);
    const [episodes, setEpisodes] = useState([]);

    const location = useLocation();
    useEffect(() => {
        async function getData() {
            const response =
                await axios.get(`https://api.themoviedb.org/3/tv/${location.state.id}?api_key=692ad17fefe3f279a5bc0dfa87752845&language=en-US
            `);

            setSeasons(response.data);
            console.log(response);
        }
        getData();
    }, []);
    async function getEpisodes(num) {
        const response = await axios.get(
            `https://api.themoviedb.org/3/tv/${location.state.id}/season/${num}?api_key=3645948f6600ca84813cb3900fecc62a&language=en-US`
        );
        setEpisodes(response.data);
    }
    return (
        <div className="tv">
            <div className="container">
                <div className="row">
                    <div className="my-auto col-6 text-start">
                        <h1 className="text-warning">{seasons.name}</h1>
                        <span className="  text-light ">
                            {seasons.overview}
                        </span>

                        <h4 className="text-danger mt-3">
                            Rating: {seasons.vote_average}/10
                        </h4>
                    </div>
                    <div className="col-1"></div>
                    <img
                        className="col-3 ms-5"
                        src={
                            "https://image.tmdb.org/t/p/w500" +
                            seasons.poster_path
                        }
                        height="400vh"
                    />
                </div>
            </div>
            <div className="container">
                <div className="row row-cols-lg-5 row-cols-md-4 row-cols-2 ">
                    {seasons.seasons &&
                        seasons.seasons.map((season) => {
                            console.log(season);
                            return (
                                <>
                                    <div
                                        key={season.id}
                                        className="card  mt-3"
                                        style={{ width: "18rem" }}
                                    >
                                        <img
                                            className="card-img-top"
                                            src={
                                                "https://image.tmdb.org/t/p/w500" +
                                                season.poster_path
                                            }
                                            alt="Card image cap"
                                        />
                                        <div className="card-body">
                                            <h5 className="card-title text-light">
                                                {season.name}
                                            </h5>
                                            <p className="card-text text-light">
                                                {season.episode_count} Episodes
                                            </p>
                                            {/* <Link
                                            to="/episodes"
                                            state={season.episode_count}
                                        > */}

                                            {/* </Link> */}
                                        </div>
                                    </div>

                                    <a
                                        onClick={() =>
                                            getEpisodes(season.season_number)
                                        }
                                        className="btn btn-warning"
                                    >
                                        Watch {season.name}
                                    </a>
                                    <div class="dropdown">
                                        <button
                                            class="btn btn-secondary dropdown-toggle"
                                            type="button"
                                            id="dropdownMenuButton2"
                                            data-bs-toggle="dropdown"
                                            aria-expanded="false"
                                        >
                                            Dropdown button
                                        </button>
                                        <ul
                                            className="dropdown-menu dropdown-menu-dark"
                                            aria-labelledby="dropdownMenuButton2"
                                        >
                                            {" "}
                                            {episodes.episodes &&
                                                episodes.episodes.map(
                                                    (episode) => {
                                                        return (
                                                            <li>
                                                                <a className="dropdown-item active">
                                                                    {
                                                                        episode.episode_number
                                                                    }
                                                                </a>
                                                            </li>
                                                        );
                                                    }
                                                )}
                                        </ul>
                                    </div>
                                </>
                            );
                        })}
                </div>{" "}
            </div>
        </div>
    );
};

export default Tv;
