import { useState } from "react";
import { useLocation } from "react-router-dom";

const Watch = () => {
    const location = useLocation();
    const [click, setClick] = useState(false);
    function clicked() {
        setClick((click) => (click = !click));
        console.log(click);
    }

    return (
        <div>
            <div
                style={{
                    position: `${click ? "relative" : "absolute"}`,
                    zIndex: "95",
                    display: "flex",
                    justifyContent: "center",
                    alignItems: "center",
                    marginTop: "25vh",
                    color: "white",
                }}
            >
                <h3>{location.state.overview}</h3>
            </div>
            <iframe
                onClick={clicked}
                src={`https://www.2embed.to/embed/tmdb/${
                    location.state.type === "movie" ? "movie" : "tv"
                }?id=${location.state.id}${
                    location.state.type === "tv" && "&s=1 NUMBER&e=1"
                }`}
                style={{
                    position: "fixed",
                    top: "5%",
                    left: 0,
                    bottom: 0,
                    right: 0,
                    width: "100%",
                    height: "95%",
                    border: "none",
                    margin: 0,
                    padding: 0,
                    overflow: "hidden",
                    zIndex: 90,
                }}
                allowFullScreen
            ></iframe>
        </div>
    );
};

export default Watch;
