import { useLocation } from "react-router-dom";

const Watch = () => {
    const location = useLocation();
    return (
        <iframe
            src={`https://www.2embed.to/embed/tmdb/${
                location.state.type === "movie" ? "movie" : "tv"
            }?id=${location.state.id}${
                location.state.type === "tv" &&
                "&s=SEASON NUMBER&e=EPISODE NUMBER"
            }`}
            style={{
                position: "fixed",
                top: "5%",
                left: 0,
                bottom: 0,
                right: 0,
                width: "100%",
                height: "95%",
                border: "none",
                margin: 0,
                padding: 0,
                overflow: "hidden",
                zIndex: 90,
            }}
            allowFullScreen
        ></iframe>
    );
};

export default Watch;
