import React from "react";
import { useState, useEffect } from "react";
import { useLocation } from "react-router-dom";

// component to display bootstrap card of the details of the episode that the user clicked on
const EpisodeDetails = ({ details }) => {
    const [img, setImg] = useState("");
    const [name, setName] = useState("");
    const [overview, setOverview] = useState("");
    const [id, setId] = useState("");
    const [num, setNum] = useState("");
    const [type, setType] = useState("");
    const location = useLocation();
    useEffect(() => {
        setImg(details.still_path);
        setName(details.name);
        setOverview(details.overview);
        setId(location.state.id);
        setNum(location.state.num);
        setType(location.state.type);
    }, [details]);
    return (
        <div className="col-12 col-md-6 col-lg-4 col-xl-3">
            <div className="card mb-4">
                <img
                    src={"https://image.tmdb.org/t/p/w500" + img}
                    className="card-img-top"
                    alt="..."
                />
                <div className="card-body">
                    <h5 className="card-title">{name}</h5>
                    <p className="card-text">{overview}</p>
                    <a
                        href={`/watch/${type}/${id}/${num}/${details.episode_number}`}
                        className="btn btn-primary"
                    >
                        Watch
                    </a>
                </div>
            </div>
        </div>
    );
};

export default EpisodeDetails;

